import React from 'react'
import Button from './Button.js'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons'
import Modal from './Modal.js'
import useModal from './useModal.js'

const ProductRow = (props) => {
    const {isShow, toggle} = useModal()
    const {name, price, onAddCart, index, products} = props

    console.log(onAddCart)
    return ( 
        <>
            <tr>
                <td onClick={toggle}><a href={"#" + name}>{name}</a></td>
                <td>{price}</td>
                <td>
                    <label htmlFor='addMoreProduct'></label>
                    <input type='number' id='addMoreProduct' name='addMoreProduct' min='1'/>    
                </td>
                <td><Button onClick={onAddCart} iconButton={ <FontAwesomeIcon icon={faShoppingCart} />}/></td>
                <td>{isShow && <Modal 
                name={name}
                price={price}
                hide={toggle}/>}
                </td>
            </tr>
        </>
    )
}

export default ProductRow;
