import React from 'react'
import PropTypes from 'prop-types';


function BoilingVerdict({celsius}) {
    if (celsius >= 100)
        return <div> L'eau Bout</div>
    else
        return <div> L'eau ne bout pas </div>
}

class Water extends React.Component {
    constructor (props) {
        super (props)
        this.handleChange = this.handleChange.bind(this)
        this.state = { eau : props.eau}
    }

    handleChange (e) {
        this.setState({
            eau: e.target.value
        })
    }

    render () {
        return (<div>
            <form>
                <label htmlFor="eau"> </label>
                <input value={this.state.eau} onChange={this.handleChange} id="eau"/>
                <label htmlFor="fahrenheit"> </label>
                <input onChange={this.handleChange} id="fahrenheit"/>
            </form>
            <BoilingVerdict celsius={parseFloat(this.state.eau)}/>
        </div>
        )
    }
}

Water.defaultProps = {
    eau: 200
}

Water.propTypes = {
    eau: PropTypes.number
}

export default Water;
