import React from 'react'
import PropTypes from 'prop-types';

class TemperatureInput extends React.ComponentÂ {
    constructor(props){
        super(props)

    }
    render () {
        return <label htmlFor={this.props.scale}></label>
    }
}

function BoilingVerdict({celsius}) {
    if (celsius >= 100)
        return <div> L'eau Bout</div>
    else
        return <div> L'eau ne bout pas </div>
}

class Water extends React.Component {
    constructor (props) {
        super (props)
        this.handleChange = this.handleChange.bind(this)
        this.state = { eau : props.eau}
    }

    handleChange (e) {
        this.setState({
            eau: e.target.value
        })
    }

    render () {
        return (<div>
            <TemperatureInput scale="c" value={this.handleChange}/>
            <TemperatureInput scale="f" value={this.handleChange}/>
            <BoilingVerdict celsius={parseFloat(this.state.eau)}/>
        </div>
        )
    }
}

Water.defaultProps = {
    eau: 200
}

Water.propTypes = {
    eau: PropTypes.number
}

export default Water;
