import React from 'react'
import ProductTable from './ProductTable.js' 
import SearchBar from './SearchBar.js'
import ProductCategoryRow from './ProductCategoryRow.js'
import ProductRow from './ProductRow.js'
import './FilterableProductTable.css'

class FilterableProductTable extends React.Component{
    constructor (props) {
        super (props)

    }



    render () {
        const {products} = this.props
        const rows = []
        let lastCategoriy = []

        products.map((product, index) => {
            let i =+ index
            if (!lastCategoriy.includes(product.category)){
                lastCategoriy.push(product.category)
                rows.push(<ProductCategoryRow key={i} category={product.category} />)
                i =+ 1
            }
            const name = product.stocked ? product.name : <span className='danger'>{product.name}</span>
            const price = product.price 
            rows.push(<ProductRow key={i} name={name} price={price}/>)
        })
        return (<div>
            <SearchBar />
            <ProductTable products={products} rows={rows}/>
        </div>)
    }
}



export default FilterableProductTable;
