import React from 'react'

const FormSelectedCategories = props => (
    <>
        <label htmlFor="category">Choose your category product</label>
        <select name="category" 
                id="category"
                multiple 
                onChange={(e) => props.onChangeCategories(Array.from(e.target.selectedOptions).map(o => o.value))}>
            {props.category}
        </select>
    </>
)
export default FormSelectedCategories;
