import React from 'react'
import PropTypes from 'prop-types';

class TemperatureInput extends React.ComponentÂ {
    constructor(props){
        super(props)
        this.state = {eau: ''}
        this.handleChange = this.handleChange.bind(this)

    }

    handleChange (e) {
        this.setState({
            eau: e.target.value
        })
    }

    render () {
        return <div>
                    <label htmlFor={this.props.scale}>{this.props.scale}</label>
                    <input value={this.props.value} id={this.props.scale} onChange={this.handleChange}/>
                </div>
    }
}

function BoilingVerdict({celsius}) {
    if (celsius >= 100)
        return <div> L'eau Bout</div>
    else
        return <div> L'eau ne bout pas </div>
}

class Water extends React.Component {
    constructor (props) {
        super (props)
        this.state = { eau : props.eau}
    }


    render () {
        return (<div>
            <TemperatureInput scale="c" />
            <TemperatureInput scale="f" />
            <BoilingVerdict celsius={parseFloat(this.state.eau)}/>
        </div>
        )
    }
}

Water.defaultProps = {
    eau: 200
}

Water.propTypes = {
    eau: PropTypes.number
}

export default Water;
