import React from 'react'
import ProductTable from './ProductTable.js' 
import SearchBar from './SearchBar.js'
import ProductCategoryRow from './ProductCategoryRow.js'
import ProductRow from './ProductRow.js'

class FilterableProductTable extends React.Component{
    constructor (props) {
        super (props)

    }



    render () {
        const {products} = this.props
        const rows = []
        let lastCategoriy = null

        products.forEach(product => {
            if (product.category !== lastCategoriy){
                lastCategoriy = product.category
                rows.push(<ProductCategoryRow category={product.category} />)
            }
            const name = products.stocked ? product.name : <span style="color:red">{product.name}</span>

            rows.push(<ProductRow name={name} price={product.price}/>)
        })
        return (<div>
            <SearchBar />
            <ProductTable products={products} rows={rows}/>
        </div>)
    }
}



export default FilterableProductTable;
